(def S 100000)

(def results (map :result (take S (doquery :smc predict [samples] :number-of-particles S))))
(def alphas (map :alpha results))
(def projections (map :sample results))
(def mus (map :mu results))
(def sigmas (map :sigma results))

(take-last 10 projections)
