(defn dist-plot-helper [dist]
  (repeatedly 10000 #(sample* dist)))

"Predictions:"

(plot/histogram projections :normalize :probability :bins 20)

"Alphas:"

(plot/compose
  (plot/histogram alphas :normalize :probability :plot-range [[(reduce min alphas) (reduce max alphas)] :all])
  (plot/histogram (dist-plot-helper alpha-prior) :normalize :probability :color "green"))

"Mus:"

(plot/compose
  (plot/histogram mus :normalize :probability :plot-range [[(reduce min mus) (reduce max mus)] :all])
  (plot/histogram (dist-plot-helper mu-prior) :normalize :probability :color "green"))

"Sigmas:"

(plot/compose
  (plot/histogram sigmas :normalize :probability :plot-range [[(reduce min sigmas) (reduce max sigmas)] :all])
  (plot/histogram (dist-plot-helper sigma-prior) :normalize :probability :color "green"))
